import { Component, OnInit } from '@angular/core';
import { Overtime } from 'src/app/models/overtime';
import { OvertimeService } from 'src/app/services/overtime.service';

@Component({
  selector: 'app-overtime',
  templateUrl: './overtime.component.html',
  styleUrls: ['./overtime.component.css']
})
export class OvertimeComponent implements OnInit{

   overtime:Overtime[] = [];
      Form: Overtime = { overTimeID: '', otDate:'', otStartTime: '',otEndTime:'',employeeID: ''};
      isEdit: boolean = false;

       constructor(private svc: OvertimeService) {}

      ngOnInit(): void {
        this.getAll();
      }

        getAll(): void {
        this.svc.getAll().subscribe(data => {
          this.overtime = data;
        });
      }

       onSubmit(): void {
        if (this.isEdit) {
          this.svc.update(this.Form.overTimeID!, this.Form).subscribe(() => {
            this.getAll();
            this.resetForm();
          });
        } else {
          const newItem = { ...this.Form, id: crypto.randomUUID() }; 
          this.svc.create(newItem).subscribe(() => {
            this.getAll();
            this.resetForm();
          });
        }
      }

        editItem(item: Overtime): void {
        this.Form = { ...item };
        this.isEdit = true;
      }

      deleteItem(id: string): void {
        if (confirm('Are you sure to delete?')) {
          this.svc.delete(id).subscribe(() => this.getAll());
        }
      }

      resetForm(): void {
        this.Form = {overTimeID: '', otDate:'', otStartTime: '',otEndTime:'',employeeID: ''};
        this.isEdit = false;
      }
}
<div class="container mt-4">
  <h2>Shift Employee Management</h2>

  <form (submit)="onSubmit()" class="mb-3">
    <div class="row mb-2">
<div class="col">
  <label>Overtime ID</label>
  <select [(ngModel)]="Form.overTimeID" name="overTimeID" class="form-control" required>
    <option value="OT001">OT001</option>
    <option value="SE002">SE002</option>
    <option value="SE003">SE003</option>
    <option value="SE004">SE004</option>
    <option value="SE005">SE005</option>
    <option value="SE006">SE006</option>
    <option value="SE007">SE007</option>
    <option value="SE008">SE008</option>
    <option value="SE009">SE009</option>
    <option value="SE010">SE010</option>
    <option value="SE011">SE011</option>
    <option value="SE012">SE012</option>
    <option value="SE013">SE013</option>
    <option value="SE014">SE014</option>
    <option value="SE015">SE015</option>
  </select>
</div>
      <div class="col">
        <label>From</label>
        <input type="date" [(ngModel)]="Form.otStartTime" name="fromDate" class="form-control" required>
      </div>
      <div class="col">
        <label>To</label>
        <input type="date" [(ngModel)]="Form.otEndTime" name="toDate" class="form-control" placeholder="To Date" required>
      </div>
    </div>

    <div class="row mb-2">
      <div class="col">
    <label>DateWiseOfficeTimeID</label>
    <select [(ngModel)]="Form.dateWiseOfficeTimeID" name="dateWiseOfficeTimeID" class="form-control" required>
    <option value="DOT001">DOT001</option>
    <option value="DOT002">DOT002</option>
    <option value="DOT003">DOT003</option>
    <option value="DOT004">DOT004</option>
    <option value="DOT005">DOT005</option>
  </select>
      </div>
      <div class="col">
        <button class="btn btn-primary" type="submit">
          {{ isEdit ? 'Update' : 'Add' }}
        </button>
        <button class="btn btn-secondary ms-2" type="button" (click)="resetForm()">Clear</button>
      </div>
    </div>
  </form>

  <table class="table table-bordered">
    <thead class="table-dark">
      <tr>
        <th> ID</th>
        <th>From Date</th>
        <th>To Date</th>
        <th>DateWise OfficeTime ID</th>
        <th style="width: 150px;">Actions</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let item of shiftEmp">
        <td>{{ item.shiftEmployeeID }}</td>
        <td>{{ item.fromDate }}</td>
        <td>{{ item.toDate }}</td>
        <td>{{ item.dateWiseOfficeTimeID }}</td>
        <td>
          <button class="btn btn-sm btn-info me-2" (click)="editItem(item)">Edit</button>
          <button class="btn btn-sm btn-danger" (click)="deleteItem(item.shiftEmployeeID!)">Delete</button>
        </td>
      </tr>
    </tbody>
  </table>
</div>
