import { Component, OnInit } from '@angular/core';
import { Overtime } from 'src/app/models/overtime';
import { OvertimeService } from 'src/app/services/overtime.service';

@Component({
  selector: 'app-overtime',
  templateUrl: './overtime.component.html',
  styleUrls: ['./overtime.component.css']
})
export class OvertimeComponent implements OnInit{
  
   overtime:Overtime[] = [];
      Form: Overtime = { overTimeID: '', otDate:'', otStartTime: '',otEndTime:'',employeeID: ''};
      isEdit: boolean = false;
    
       constructor(private svc: OvertimeService) {}
    
      ngOnInit(): void {
        this.getAll();
      }
    
        getAll(): void {
  this.svc.getAll().subscribe(data => {
    this.overtime = data;


    const maxId = this.overtime.reduce((max, item) => {
      const num = parseInt(item.overTimeID!.replace('OT', ''), 10);
      return isNaN(num) ? max : Math.max(max, num);
    }, 0);


    const nextId = `OT${(maxId + 1).toString().padStart(3, '0')}`;
    this.Form.overTimeID = nextId;
  });
}

    
       onSubmit(): void {
        if (this.isEdit) {
          this.svc.update(this.Form.overTimeID!, this.Form).subscribe(() => {
            this.getAll();
            this.resetForm();
          });
        } else {
          const newItem = { ...this.Form, id: crypto.randomUUID() }; 
          this.svc.create(newItem).subscribe(() => {
            this.getAll();
            this.resetForm();
          });
        }
      }
    
        editItem(item: Overtime): void {
        this.Form = { ...item };
        this.isEdit = true;
      }
    
      deleteItem(id: string): void {
        if (confirm('Are you sure to delete?')) {
          this.svc.delete(id).subscribe(() => this.getAll());
        }
      }
    
      resetForm(): void {
        this.Form = {overTimeID: '', otDate:'', otStartTime: '',otEndTime:'',employeeID: ''};
        this.isEdit = false;
      }
}
<div class="container mt-4">
  <h2>Over Time Management</h2>

  <form (submit)="onSubmit()" class="mb-3">
    <div class="row mb-2">
<div class="col">
  <label>Overtime ID</label>
  <input [(ngModel)]="Form.overTimeID"
         name="overTimeID"
         class="form-control"
         placeholder="OT001"
         readonly> <!-- This prevents manual editing -->
</div>
      <div class="col">
        <label>Date</label>
        <input type="date" [(ngModel)]="Form.otDate" name="otDate" class="form-control" required>
      </div>
      <div class="col">
        <label>OT Start Time</label>
        <input type="text" [(ngModel)]="Form.otStartTime" name="otStartTime" class="form-control" required>
      </div>
       <div class="col">
        <label>OT End Time</label>
        <input type="text" [(ngModel)]="Form.otEndTime" name="otEndTime" class="form-control"  required>
      </div>
    </div>

    <div class="row mb-2">
      <div class="col">
    <label>Employe ID</label>
    <select [(ngModel)]="Form.employeeID" name="employeeID" class="form-control" required>
    <option value="E001">E001</option>
    <option value="E002">E002</option>
    <option value="E003">E003</option>
    <option value="E004">E004</option>
    <option value="E005">E005</option>
  </select>
      </div>
      <div class="col">
        <button class="btn btn-primary" type="submit">
          {{ isEdit ? 'Update' : 'Add' }}
        </button>
        <button class="btn btn-secondary ms-2" type="button" (click)="resetForm()">Clear</button>
      </div>
    </div>
  </form>

  <table class="table table-bordered">
    <thead class="table-dark">
      <tr>
        <th>Over Time ID</th>
        <th>OT Date</th>
        <th>Ot Start Date</th>
        <th>OT End Date</th>
        <th>Employee ID</th>
        <th style="width: 150px;">Actions</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let item of overtime">
        <td>{{ item.overTimeID }}</td>
        <td>{{ item.otDate }}</td>
        <td>{{ item.otStartTime }}</td>
        <td>{{ item.otEndTime }}</td>
         <td>{{ item.employeeID }}</td>
        <td>
          <button class="btn btn-sm btn-info me-2" (click)="editItem(item)">Edit</button>
          <button class="btn btn-sm btn-danger" (click)="deleteItem(item.overTimeID!)">Delete</button>
        </td>
      </tr>
    </tbody>
  </table>
</div>
