import { Component, OnInit } from '@angular/core';
import { ShiftEmployee } from 'src/app/models/shift-employee';

@Component({
  selector: 'app-shift-employee',
  templateUrl: './shift-employee.component.html',
  styleUrls: ['./shift-employee.component.css']
})
export class ShiftEmployeeComponent implements OnInit{

    shiftEmp:ShiftEmployee[] = [];
    Form: ShiftEmployee = { shiftEmployeeID: '', fromDate:0, salaryHeadName: '',amount:0,
      employeeID:''
     };
    isEdit: boolean = false;

     constructor(private svc: SalaryEntryService) {}

    ngOnInit(): void {
      this.getAllSalaryEntry();
    }

      getAllSalaryEntry(): void {
      this.svc.getAll().subscribe(data => {
        this.salaryEntry = data;
      });
    }

     onSubmit(): void {
      if (this.isEdit) {
        this.svc.update(this.Form.salaryEntryID!, this.Form).subscribe(() => {
          this.getAllSalaryEntry();
          this.resetForm();
        });
      } else {
        const newItem = { ...this.Form, id: crypto.randomUUID() }; // If you manually generate string id
        this.svc.create(newItem).subscribe(() => {
          this.getAllSalaryEntry();
          this.resetForm();
        });
      }
    }

      editItem(item: SalaryEntry): void {
      this.Form = { ...item };
      this.isEdit = true;
    }

    deleteItem(id: string): void {
      if (confirm('Are you sure to delete?')) {
        this.svc.delete(id).subscribe(() => this.getAllSalaryEntry());
      }
    }

    resetForm(): void {
      this.Form = { salaryEntryID: '', applyDate:'', salaryHeadName: '',amount:0,
      employeeID:''};
      this.isEdit = false;
    }
}
